import React from 'react';
import $ from 'jquery';
import '../stylesheets/login.scss';

export default class Login extends React.Component {
  state = {
    email: "test123@example.com",
    password: "password"
  }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleLogin = (e) => {
    e.preventDefault();
    $("login-errors").empty();

    $.ajax({
      type: 'POST',
      url: 'http://localhost:3001/auth/sign_in',
      data: {
        email: this.state.email,
        password: this.state.password
      },
      success: (response, status, jqXHR) => {
        sessionStorage.setItem('user',
          JSON.stringify({
            'access-token': jqXHR.getResponseHeader('access-token'),
            client: jqXHR.getResponseHeader('client'),
            uid: response.data.uid
          })
        );
  // use headers: JSON.parse(sessionStorage.user) in ajax calls to api
        this.props.history.push('/');
      },
      error: resp => {
        resp.responseJSON.errors.map(error => $("#login-errors").append(`<li>${error}</li>`))
      }
    })
  }

  render () {
    return (
      <div>
        <h2>Sign in</h2>
        <div id="login-errors" className="errors">
        </div>

        <form onSubmit={this.handleLogin} >
          <input name="email" value={this.state.email} onChange={this.handleChange} />
          <input name="password" type="password" value={this.state.password} onChange={this.handleChange} />
          <input type="submit"/>
        </form>
      </div>
    )
  }
}
