import React from 'react';
import {Calendar, momentLocalizer} from 'react-big-calendar'
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

// Setup the localizer by providing the moment (or globalize) Object
// to the correct localizer.
const localizer = momentLocalizer(moment) // or globalizeLocalizer
const myEventsList = []

class CalendarCont extends React.Component {
  state = {
    events: null
  }
  
  // componentDidMount(){
  //   // TODO static url right now for testing, probably move this to props? or something
  //   fetch(`http://localhost:3001/api/districts/1/schools/1`)
  //     .then(resp => resp.json())
  //     .then(data => {
  //       let events = []
  //       data.events.map(e => {
  //         return events.push({
  //           title: e.title,
  //           start: Date.parse(e.start),
  //           end: Date.parse(e.end),
  //           allDay: e.allDay
  //         })
  //       })
  //       this.setState({events: events}, () => console.log("state updated"));
  //     })
  // }

  render(){
    return (
      <div style={{
        "height": "80vh",
        "margin": "20px"}}>
  
        <Calendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
        />
      </div>
    )
  }
}

const now = new Date()
const events = [
  {
    id: 0,
    title: 'All Day Event very long title',
    allDay: true,
    start: new Date(2019, 9, 0),
    end: new Date(2019, 9, 1),
  },
  {
    id: 1,
    title: 'Long Event',
    start: new Date(2019, 9, 7),
    end: new Date(2019, 9, 10),
  },

  {
    id: 2,
    title: 'DTS STARTS',
    start: new Date(2016, 2, 13, 0, 0, 0),
    end: new Date(2016, 2, 20, 0, 0, 0),
  },

  {
    id: 3,
    title: 'DTS ENDS',
    start: new Date(2016, 10, 6, 0, 0, 0),
    end: new Date(2016, 10, 13, 0, 0, 0),
  },

  {
    id: 4,
    title: 'Some Event',
    start: new Date(2019, 9, 9, 0, 0, 0),
    end: new Date(2019, 9, 10, 0, 0, 0),
  },
  {
    id: 5,
    title: 'Conference',
    start: new Date(2019, 9, 11),
    end: new Date(2019, 9, 13),
    desc: 'Big conference for important people',
  },
  {
    id: 6,
    title: 'Meeting',
    start: new Date(2019, 9, 12, 10, 30, 0, 0),
    end: new Date(2019, 9, 12, 12, 30, 0, 0),
    desc: 'Pre-meeting meeting, to prepare for the meeting',
  },
  {
    id: 7,
    title: 'Lunch',
    start: new Date(2019, 9, 12, 12, 0, 0, 0),
    end: new Date(2019, 9, 12, 13, 0, 0, 0),
    desc: 'Power lunch',
  },
  {
    id: 8,
    title: 'Meeting',
    start: new Date(2019, 9, 12, 14, 0, 0, 0),
    end: new Date(2019, 9, 12, 15, 0, 0, 0),
  },
  {
    id: 9,
    title: 'Happy Hour',
    start: new Date(2019, 9, 12, 17, 0, 0, 0),
    end: new Date(2019, 9, 12, 17, 30, 0, 0),
    desc: 'Most important meal of the day',
  },
  {
    id: 10,
    title: 'Dinner',
    start: new Date(2019, 9, 12, 20, 0, 0, 0),
    end: new Date(2019, 9, 12, 21, 0, 0, 0),
  },
  {
    id: 11,
    title: 'Birthday Party',
    start: new Date(2019, 9, 13, 7, 0, 0),
    end: new Date(2019, 9, 13, 10, 30, 0),
  },
  {
    id: 12,
    title: 'Late Night Event',
    start: new Date(2019, 9, 17, 19, 30, 0),
    end: new Date(2019, 9, 18, 2, 0, 0),
  },
  {
    id: 12.5,
    title: 'Late Same Night Event',
    start: new Date(2019, 9, 17, 19, 30, 0),
    end: new Date(2019, 9, 17, 23, 30, 0),
  },
  {
    id: 13,
    title: 'Multi-day Event',
    start: new Date(2019, 9, 20, 19, 30, 0),
    end: new Date(2019, 9, 22, 2, 0, 0),
  },
  {
    id: 14,
    title: 'Today',
    start: new Date(new Date().setHours(new Date().getHours() - 3)),
    end: new Date(new Date().setHours(new Date().getHours() + 3)),
  },
  {
    id: 15,
    title: 'Point in Time Event',
    start: now,
    end: now,
  },
]

export default CalendarCont;
