import React from 'react';
import {Link} from 'react-router-dom';

const NavigationMenu = props => {
  const content = $("#nav-contact")
  function toggleContact() {
    if (content.style.display === "block") {
      content.style.display = "none";
    } else {
      content.style.display = "block";
    }
  }

  return(
    <div id="navMenu">
      <Link to={`/districts/${props.district_id}/selector`}>Select a different School</Link>
      <Link to={`/districts/${props.district_id}`}>District Page</Link>
      <Link to={`/districts/${props.district_id}/calendar`}>District Calendar</Link>
      <a href="#">School Info</a>
      <div id="nav-contact" class="collapsible" onClick={toggleContact}>
        Phone: {props.school.phone}<br />
        Address: {props.school.address}<br />
        <a href={props.school.homepage_url}>Homepage</a>
      </div>
    </div>
  )
}

export default NavigationMenu;
