import React from 'react';

export default class Login extends React.Component {
  handleLogin = (e) => {
    e.preventDefault();
  }
  
  render () {
    return (
      <div>
        <h2>Sign in</h2>
        <form onSubmit={this.handleLogin} >
          <input name="email" />
          <input name="password" type="password" />
          <input type="submit"/>
        </form>
      </div>
    )
  }
}
